import React, { Component } from "react";

export class NewsItem extends Component {
  render() {
    let {title,description,imageUrl,newsUrl,author,date,source}=this.props;
    return (
      <div className="my-3">
        <div className="card">
          <div>
          <span className=" badge rounded-pill bg-danger" style={{}}>
              {source}
  </span>
          </div>
       
          <img src={!imageUrl?"https://www.hindustantimes.com/ht-img/img/2023/08/06/1600x900/Collage_Maker-06-Aug-2023-09-30-AM-7997_1691296932280_1691296939649.jpg":imageUrl} alt="Top News Daily"/>
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}...</p>
            <p className="card-text"><small className="text-body-secondary">By {!author?"Unknown":author} on {new Date(date).toGMTString()}</small></p>
            <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-sm btn-dark">
              Read More
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItem;
